mapper.select:
SELECT b.bank_nm, a.acct_no, (SELECT bal FROM inout_past WHERE trsc_dt = #{startDt} AND acct_no = a.acct_no ORDER BY trsc_tm DESC LIMIT 1) AS before_bal, SUM(CASE WHEN t.inout_dv = 1 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN 1 ELSE 0 END) AS in_cnt, SUM(CASE WHEN t.inout_dv = 1 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN t.trsc_amt ELSE 0 END) AS in_sum, SUM(CASE WHEN t.inout_dv = 2 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN 1 ELSE 0 END) AS out_cnt, SUM(CASE WHEN t.inout_dv = 2 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN t.trsc_amt ELSE 0 END) AS out_sum, (SELECT bal FROM inout_past WHERE trsc_dt = #{endDt} AND acct_no = a.acct_no ORDER BY trsc_tm DESC LIMIT 1) AS after_bal FROM account a JOIN bank_code b ON a.bank_cd = b.bank_cd LEFT JOIN inout_past t ON a.acct_no = t.acct_no WHERE a.acct_dv IN ('01', '02', '03')
AND a.bank_cd = #{bankCd}
AND a.acct_no = #{acctNo}
GROUP BY b.bank_nm, a.acct_no ORDER BY a.bank_cd asc

SELECT b.bank_nm, a.acct_no, (SELECT bal FROM inout_past WHERE trsc_dt = #{startDt} AND acct_no = a.acct_no ORDER BY trsc_tm DESC LIMIT 1) AS before_bal, SUM(CASE WHEN t.inout_dv = 1 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN 1 ELSE 0 END) AS in_cnt, SUM(CASE WHEN t.inout_dv = 1 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN t.trsc_amt ELSE 0 END) AS in_sum, SUM(CASE WHEN t.inout_dv = 2 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN 1 ELSE 0 END) AS out_cnt, SUM(CASE WHEN t.inout_dv = 2 AND t.trsc_dt >= #{startDt} AND t.trsc_dt <= #{endDt} THEN t.trsc_amt ELSE 0 END) AS out_sum, (SELECT bal FROM inout_past WHERE trsc_dt = #{endDt} AND acct_no = a.acct_no ORDER BY trsc_tm DESC LIMIT 1) AS after_bal FROM account a JOIN bank_code b ON a.bank_cd = b.bank_cd JOIN user_account ua ON a.acct_no = ua.acct_no LEFT JOIN inout_past t ON a.acct_no = t.acct_no WHERE a.acct_dv IN ('01', '02', '03')
AND a.bank_cd = #{bankCd}
AND a.acct_no = #{acctNo}
AND ua.user_no = #{userNo} GROUP BY b.bank_nm, a.acct_no ORDER BY a.bank_cd asc

SELECT MAX(trsc_dt)
FROM inout_past
WHERE acct_no = #{acctNo} AND trsc_dt < #{date}
