mapper.update:
UPDATE user 
SET password=#{password}, name=#{name}, dept=#{dept}, 
	email=#{email}, phone=#{phone}
WHERE user_no=#{userNo}

mapper.select:
SELECT id, name, dept, email, phone 
FROM user 
WHERE user_no=#{userNo}

SELECT user_no, user_code, id, name, dept, email, phone
FROM user
ORDER BY user_no ASC;
